        [Test]
        [TestCase(LmsType.LMS_SHA256_M32_H5, LmotsType.LMOTS_SHA256_N32_W1)]
        [TestCase(LmsType.LMS_SHA256_M32_H5, LmotsType.LMOTS_SHA256_N32_W2)]
        [TestCase(LmsType.LMS_SHA256_M32_H5, LmotsType.LMOTS_SHA256_N32_W4)]
        [TestCase(LmsType.LMS_SHA256_M32_H5, LmotsType.LMOTS_SHA256_N32_W8)]
        public async Task ShouldVerifySignatureCorrectlyRandomlyGenerated1Layer(LmsType lmsType, LmotsType lmotsType)
        {
            var msg = new BitString("7ab290ec2c6649a14abcdef093783242");

            int layers = 1;
            LmsType[] types = { lmsType };
            LmotsType[] lmotsTypes = { lmotsType };

            var hss = new Hss(layers, types, lmotsTypes);

            var keyPair = await hss.GenerateHssKeyPairAsync();

            var sig = await hss.GenerateHssSignatureAsync(msg, keyPair);

            var verify = hss.VerifyHssSignature(msg, keyPair.PublicKey, sig.Signature);

            Assert.That(verify.Success);
        }

