        [Test]
        [TestCase(LmsType.LMS_SHA256_M32_H5, LmotsType.LMOTS_SHA256_N32_W4)]
        public async Task ShouldUpdateKeyProperly1Layer(LmsType lmsType, LmotsType lmotsType)
        {
            var msg = new BitString("7ab290ec2c6649a14abcdef093783242");

            int layers = 1;
            LmsType[] types = { lmsType };
            LmotsType[] lmotsTypes = { lmotsType };

            var hss = new Hss(layers, types, lmotsTypes);

            var keyPair = await hss.GenerateHssKeyPairAsync();

            var sig = await hss.GenerateHssSignatureAsync(msg, keyPair);

            // should be 0 use
            Assert.That(keyPair.PrivateKey.PrivateKeys[0].Q == 0);

            // use it 31 more times
            for (int i = 0; i < 31; i++)
            {
                keyPair = await hss.UpdateKeyPairOneStepAsync(keyPair);
            }

            Assert.That(!keyPair.Expired);

            keyPair = await hss.UpdateKeyPairOneStepAsync(keyPair);

            Assert.That(keyPair.Expired);
        }

        [Test]
        [TestCase(LmsType.LMS_SHA256_M32_H5, LmotsType.LMOTS_SHA256_N32_W4)]
        public async Task ShouldUpdateKeyProperly2Layer(LmsType lmsType, LmotsType lmotsType)
        {
            var msg = new BitString("7ab290ec2c6649a14abcdef093783242");

            int layers = 2;
            LmsType[] types = { lmsType, lmsType };
            LmotsType[] lmotsTypes = { lmotsType, lmotsType };

            var hss = new Hss(layers, types, lmotsTypes);

            var keyPair = await hss.GenerateHssKeyPairAsync();

            var sig = await hss.GenerateHssSignatureAsync(msg, keyPair);

            // should be 0 use
            Assert.AreEqual(0, keyPair.PrivateKey.PrivateKeys[1].Q);

            // use it 32 more times
            for (int i = 0; i < 32; i++)
            {
                keyPair = await hss.UpdateKeyPairOneStepAsync(keyPair);
            }

            Assert.That(!keyPair.Expired);
            Assert.AreEqual(0, keyPair.PrivateKey.PrivateKeys[1].Q);
            Assert.AreEqual(1, keyPair.PrivateKey.PrivateKeys[0].Q);

            keyPair = await hss.UpdateKeyPairOneStepAsync(keyPair);

            Assert.That(!keyPair.Expired);

            Assert.That(keyPair.PrivateKey.PrivateKeys[0].Q == 1);
            Assert.That(keyPair.PrivateKey.PrivateKeys[1].Q == 1);

            // use it some more
            for (int i = 0; i < 32 * 31 - 2; i++)
            {
                keyPair = await hss.UpdateKeyPairOneStepAsync(keyPair);
            }

            Assert.That(!keyPair.Expired);

            Assert.AreEqual(31, keyPair.PrivateKey.PrivateKeys[0].Q);
            Assert.AreEqual(31, keyPair.PrivateKey.PrivateKeys[1].Q);

            keyPair = await hss.UpdateKeyPairOneStepAsync(keyPair);

            Assert.That(keyPair.Expired);
        }

