        public async Task ShouldGenerateWithProperKeyUpdateOneStep(int advance, string seed, string i, string expectedPublic, string msg, string expectedSignature)
        {
            var SEED = new BitString(seed);
            var I = new BitString(i);
            var expectedPublicKey = new BitString(expectedPublic);
            var expectedSig = new BitString(expectedSignature);
            var message = new BitString(msg);

            int layers = 2;
            LmsType[] types = { LmsType.LMS_SHA256_M32_H5, LmsType.LMS_SHA256_M32_H5 };
            LmotsType[] lmotsTypes = { LmotsType.LMOTS_SHA256_N32_W8, LmotsType.LMOTS_SHA256_N32_W8 };

