            var keyPair = await hss.GenerateHssKeyPairAsync();

            Assert.AreEqual(expectedPublicKey, keyPair.PublicKey);

            for (int j = 0; j < advance; j++)
            {
                keyPair = await hss.UpdateKeyPairOneStepAsync(keyPair);
            }

            var sig = await hss.GenerateHssSignatureAsync(message, keyPair);

            Assert.AreEqual(expectedSig.ToHex(), sig.Signature.ToHex());

            var verify = hss.VerifyHssSignature(message, keyPair.PublicKey, sig.Signature);

            Assert.That(verify.Success);
        }

