using NIST.CVP.ACVTS.Libraries.Crypto.Common.Asymmetric.LMS.Enums;
using NIST.CVP.ACVTS.Libraries.Math;
using NIST.CVP.ACVTS.Libraries.Math.Entropy;
using NUnit.Framework;

namespace NIST.CVP.ACVTS.Libraries.Crypto.LMS.Tests
{
    [Ignore("Replacing with native implementation")]
    public class LmotsTests
    {
        [Test]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W1)]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W2)]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W4)]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W8)]
        public void ShouldVerifySignatureCorrectlyRandomlyGenerated(LmotsType type)
        {
            var lmots = new Lmots(type);

            var priv = lmots.GenerateLmotsPrivateKey(new BitString("00000005"), new BitString("61a5d57d37f5e46bfb7520806b07a1b8"));

            var pub = lmots.GenerateLmotsPublicKey(priv);

            var msg = new BitString("7ab290ec2c6649a14abcdef093783242");

            var sig = lmots.GenerateLmotsSignature(msg, priv);

            var verify = lmots.VerifyLmotsSignature(sig, new BitString(pub), msg);

            Assert.That(verify.Success);
        }

        [Test]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W1, "")]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W2, "")]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W4, "")]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W8, "")]
        public void ShouldPrivateKeyGenKAT(LmotsType type, string expected)
        {
            var lmots = new Lmots(type, EntropyProviderTypes.Testable);

            var priv = lmots.GenerateLmotsPrivateKey(new BitString("00000005"),
                new BitString("61a5d57d37f5e46bfb7520806b07a1b8"),
                new BitString("d9cc64934b53cbdb154b9a4fa0150048bc2fe82b0ec76125c198ec1bf718807b"));

            Assert.AreEqual(new BitString(expected).ToHex(), priv.ToHex());
        }

        [Test]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W1, "", "0000000161a5d57d37f5e46bfb7520806b07a1b800000005690f21f69dea331a49f0c694ddb1d588d64f04df54467b84adcd64f34fdecffc")]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W2, "", "0000000261a5d57d37f5e46bfb7520806b07a1b8000000057deba0b6933e71adb376afc3e0fce069a778554da33bcfb83ee494a21fd1405b")]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W4, "", "0000000361a5d57d37f5e46bfb7520806b07a1b8000000050c48741ae6b15be40f8c32503f06ee093e7eeddfab7a933f13b9732ebcb478d8")]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W8, "", "0000000461a5d57d37f5e46bfb7520806b07a1b800000005db16a74bee9025a2fa082db03bfb63f554d819f53e82bcc9de7799ef425a0dba")]
        public void ShouldPublicKeyGenKAT(LmotsType type, string priv, string expected)
        {
            var lmots = new Lmots(type, EntropyProviderTypes.Testable);

            var pub = lmots.GenerateLmotsPublicKey(new BitString(priv));

            Assert.That(new BitString(pub).Equals(new BitString(expected)));
        }

        [Test]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W1, "", "")]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W2, "", "")]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W4, "", "")]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W8, "", "")]
        public void ShouldSigGenKAT(LmotsType type, string priv, string expected)
        {
            var lmots = new Lmots(type, EntropyProviderTypes.Testable);

            var sig = lmots.GenerateLmotsSignature(new BitString("7ab290ec2c6649a14abcdef093783242"),
                new BitString(priv), new BitString("d9cc64934b53cbdb154b9a4fa0150048bc2fe82b0ec76125c198ec1bf718807b"));

            Assert.AreEqual(new BitString(expected).ToHex(), sig.ToHex());
        }

        [Test]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W1, "", "0000000161a5d57d37f5e46bfb7520806b07a1b800000005690f21f69dea331a49f0c694ddb1d588d64f04df54467b84adcd64f34fdecffc")]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W2, "", "0000000261a5d57d37f5e46bfb7520806b07a1b8000000057deba0b6933e71adb376afc3e0fce069a778554da33bcfb83ee494a21fd1405b")]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W4, "", "0000000361a5d57d37f5e46bfb7520806b07a1b8000000050c48741ae6b15be40f8c32503f06ee093e7eeddfab7a933f13b9732ebcb478d8")]
        [TestCase(LmotsType.LMOTS_SHA256_N32_W8, "", "0000000461a5d57d37f5e46bfb7520806b07a1b800000005db16a74bee9025a2fa082db03bfb63f554d819f53e82bcc9de7799ef425a0dba")]
        public void ShouldVerifyKAT(LmotsType type, string sig, string pub)
        {
            var lmots = new Lmots(type, EntropyProviderTypes.Testable);

            var verify = lmots.VerifyLmotsSignature(new BitString(sig),
                new BitString(pub), new BitString("7ab290ec2c6649a14abcdef093783242"));

            Assert.That(verify.Success);
        }
    }
}
