        private Lms _subject;
        private ILmsKeyPairFactory _lmsKeyPairFactory;
        private ILmOtsRandomizerC _randomizerC;

        [OneTimeSetUp]
        public void Setup()
        {
            var shaFactory = new NativeShaFactory();
            _lmsKeyPairFactory = new LmsKeyPairFactory(new LmOtsKeyPairFactory(shaFactory), shaFactory);
            _randomizerC = new LmOtsPseudoRandomizerC(shaFactory);
            var lmOtsKeyPairFactory = new LmOtsKeyPairFactory(shaFactory);

            var lmOts = new LmOts(shaFactory);
            _subject = new Lms(lmOtsKeyPairFactory, lmOts, shaFactory);
        }

        [Test]
        [TestCase(LmsMode.LMS_SHA256_M24_H5, LmOtsMode.LMOTS_SHA256_N24_W1, "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H5, LmOtsMode.LMOTS_SHA256_N24_W2, "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H5, LmOtsMode.LMOTS_SHA256_N24_W4, "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H5, LmOtsMode.LMOTS_SHA256_N24_W8, "")]
        
        [TestCase(LmsMode.LMS_SHA256_M24_H10, LmOtsMode.LMOTS_SHA256_N24_W1, "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H10, LmOtsMode.LMOTS_SHA256_N24_W2, "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H10, LmOtsMode.LMOTS_SHA256_N24_W4, "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H10, LmOtsMode.LMOTS_SHA256_N24_W8, "")]
        public void WhenGivenDefaultParametersOnSmallTreeShouldSignAndVerifySuccessfully(LmsMode lmsMode, LmOtsMode lmOtsMode, string signatureHex)
        {
            var lmsKeyPair = _lmsKeyPairFactory.GetKeyPair(lmsMode, lmOtsMode, new byte[16], new byte[24]);
            
            var message = new byte[32];

            var signature = _subject.Sign(lmsKeyPair.PrivateKey, _randomizerC, message);
            var verify = _subject.Verify(lmsKeyPair.PublicKey, signature.Signature, message);

            Console.WriteLine(new BitString(signature.Signature).ToHex());
            Assert.AreEqual(signatureHex, new BitString(signature.Signature).ToHex());
            Assert.IsTrue(verify.Success);
        }
        
        [Test]
        [TestCase(LmsMode.LMS_SHA256_M24_H5, LmOtsMode.LMOTS_SHA256_N24_W1, "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H5, LmOtsMode.LMOTS_SHA256_N24_W2, "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H5, LmOtsMode.LMOTS_SHA256_N24_W4, "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H5, LmOtsMode.LMOTS_SHA256_N24_W8, "")]
       
        [TestCase(LmsMode.LMS_SHA256_M24_H10, LmOtsMode.LMOTS_SHA256_N24_W1, "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H10, LmOtsMode.LMOTS_SHA256_N24_W2, "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H10, LmOtsMode.LMOTS_SHA256_N24_W4, "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H10, LmOtsMode.LMOTS_SHA256_N24_W8, "")]
        public void WhenGivenPrecomputedTree_ShouldSignAndVerifySuccessfully(LmsMode lmsMode, LmOtsMode lmOtsMode, string signatureHex)
        {
            var lmsKeyPair = LmsTestKeyPairProvider.GetPrecomputedTree(lmsMode, lmOtsMode);
            
            var message = new byte[32];

            var signature = _subject.Sign(lmsKeyPair.PrivateKey, _randomizerC, message);
            var verify = _subject.Verify(lmsKeyPair.PublicKey, signature.Signature, message);

            Console.WriteLine(new BitString(signature.Signature).ToHex());
            Assert.AreEqual(signatureHex, new BitString(signature.Signature).ToHex());
            Assert.IsTrue(verify.Success);
        }
        
        // The trees here were likely generated using a 32-byte seed rather than a 24-byte seed
        [Test]
        [TestCase(LmsMode.LMS_SHA256_M24_H25, LmOtsMode.LMOTS_SHA256_N24_W1, 0, "LMS_SHA256_M24_H25_LMOTS_SHA256_N24_W1_Seed_0.txt", "")]
        [TestCase(LmsMode.LMS_SHA256_M24_H25, LmOtsMode.LMOTS_SHA256_N24_W1, 1, "LMS_SHA256_M24_H25_LMOTS_SHA256_N24_W1_Seed_1.txt", "")]
        //[TestCase(LmsMode.LMS_SHA256_M24_H25, LmOtsMode.LMOTS_SHA256_N24_W8, 0, "LMS_SHA256_M24_H25_LMOTS_SHA256_N24_W8_Seed_0.txt", "")]
        public void WhenGivenVeryLargePrecomputedTree_ShouldSignAndVerifySuccessfully(LmsMode lmsMode, LmOtsMode lmOtsMode, byte lastOctet, string filePath, string expectedSignature)
        {
            var path = Utilities.GetConsistentTestingStartPath(GetType(), @"PrecomputedTrees\");
            path = Path.Combine(path, filePath);
            
            var lmsKeyPair = LmsTestKeyPairProvider.GetPrecomputedTreeFromFile(lmsMode, lmOtsMode, lastOctet, path);

            var message = new byte[32];

            var signature = _subject.Sign(lmsKeyPair.PrivateKey, _randomizerC, message);
            Console.WriteLine(new BitString(signature.Signature).ToHex());
            var verify = _subject.Verify(lmsKeyPair.PublicKey, signature.Signature, message);
            
            Assert.AreEqual(expectedSignature, new BitString(signature.Signature).ToHex());
            Assert.IsTrue(verify.Success);
        }

