            "")]
        public void RfcTestVector_ShouldLmsVerify(string key, string message, string signature)
        {
            var shaFactory = new NativeShaFactory();
            var lmOts = new LmOts(shaFactory);
            var lmOtsKeyPairFactory = new LmOtsKeyPairFactory(shaFactory);
            var lms = new Lms(lmOtsKeyPairFactory, lmOts, shaFactory);

            var result = lms.Verify(
                new LmsPublicKey(new BitString(key).ToBytes()),
                new BitString(signature).ToBytes(),
                new BitString(message).ToBytes());

            Assert.True(result.Success);
        }

        [Test]
        [TestCase(LmsMode.LMS_SHA256_M24_H5, 1, true)]
        [TestCase(LmsMode.LMS_SHA256_M24_H5, 32, true)]
        [TestCase(LmsMode.LMS_SHA256_M24_H5, 33, false)]
        public void WhenLmsTreeProducesMoreSignaturesThanItsHeightAllows_ShouldReturnNullSignature(LmsMode lmsMode, int signaturesToProduce, bool expectedValidSignature)
        {
            var keyPair = _lmsKeyPairFactory.GetKeyPair(lmsMode, LmOtsMode.LMOTS_SHA256_N24_W1, new byte[16], new byte[24]);

            var message = new byte[16];

            // We want to generate signatures up to the amount minus 1,
            // just for a separate evaluation of "the last requested signature"
            for (var i = 0; i < signaturesToProduce - 1; i++)
            {
                var loopSignature = _subject.Sign(keyPair.PrivateKey, _randomizerC, message);

                Assert.IsFalse(loopSignature.Exhausted);
            }

            var lastSignatureAttempt = _subject.Sign(keyPair.PrivateKey, _randomizerC, message);

            if (expectedValidSignature)
            {
                Assert.IsFalse(lastSignatureAttempt.Exhausted);
                Assert.IsNotNull(lastSignatureAttempt.Signature);
            }
            else
            {
                Assert.IsTrue(lastSignatureAttempt.Exhausted);
                Assert.IsNull(lastSignatureAttempt.Signature);
            }
        }

        [Test]
        [Ignore("This test was run just to get timing prior to optimization. Run time was 15 hours. 6.5 hours on Chris machine (for H25_W1)")]
        public void ShouldGenerateLmsTreeHeight25()
        {
            var seed = new byte[24];
            seed[23] = 0;
            var keyPair =  _lmsKeyPairFactory.GetKeyPair(LmsMode.LMS_SHA256_M24_H25, LmOtsMode.LMOTS_SHA256_N24_W8, new byte[16], seed, 10);

            var format = "0000";
            using StreamWriter file = new StreamWriter("/Users/ctc/Documents/LMS_SHA256_M24_H25_LMOTS_SHA256_N24_W8_Seed_0.txt");
            file.WriteLine($"Public Key: {new BitString(keyPair.PublicKey.Key).ToHex()}");
            file.WriteLine("0000: ");   // intentional empty line
            
            for (int i = 1; i < (2 << 10); i++)
            {
                if (keyPair.PrivateKey.HasPrecomputedHash(i))
                {
                    var node = keyPair.PrivateKey.GetTreeNodeAtIndex(i);
                    file.WriteLine($"{i.ToString(format)}: {new BitString(node).ToHex()}");    
                }
            }
            
            Assert.IsNotNull(keyPair.PublicKey.Key);
        }
